<?php

declare(strict_types=1);

namespace Tests\Pledg\Bundle\PaymentBundle\Unit\Notification;

class TransferContentBuilder
{
    /** @var string */
    protected $jsonContent;

    public function withValidContent(): self
    {
        $this->jsonContent = '{"signature":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..aQ9sLecFiD4yazL_T2hA-WMdbCQT_I157tn0P2sIoMc"}';

        return $this;
    }

    public function withInvalidSignature(): self
    {
        $this->jsonContent = '{"signature":"eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..4s1WhHfpbi9ykxiWNWLAgaiH6g5JwHUB5uE2m2Rc0q"}';

        return $this;
    }

    public function build(): array
    {
        return json_decode($this->jsonContent, true, 512, \JSON_THROW_ON_ERROR);
    }
}
